export default function validatePreferences(values, preferences) {
  let errors = {}

  console.log('in validate preferences')
  console.log(values)
  console.log(preferences)

  //now here you just gotta map over the preferences against value.newCategory or whatever its called
  //and ya you just loop over it and if you find it then you say no no that category needs to be U N I Q U E

  const categoryIsAlreadyThere = (preferences.indexOf(values.newCategory) > -1)
  if (categoryIsAlreadyThere) {
    console.log('the category is already here')
  } else {
    console.log('category not detected')
  }

  // email related errors
  if (!values.fact) {
    errors.fact = "Email required"
  }

  return errors;
}
