export default function validatePreferences(values, preferences) {
  let errors = {}

  // const categoryIsAlreadyThere = (preferences.indexOf(values.newCategory) > -1)

  //hah if i were to write my own way of doing this becuase it's not supported in ie9... well that's pretty silly, because who the fuck is using that?
  //but if I had to... i mean this whole fucking app probably wouldn't run in ie9, would it? well idk about that...

  const categoryIsAlreadyThere = preferences.map(pref => pref === values.newCategory && true)

  if (categoryIsAlreadyThere) {
    errors.category = "This category is already in the list... "
  }

  if (!values.fact) {
    errors.fact = "Email required"
  }

  return errors
}
