import React from 'react'
import FirebaseContext from '../firebase/context'
import Expense from './Expense'


function Expenses() {
  const { firebase, user } = React.useContext(FirebaseContext)
  const [expenses, setExpenses] = React.useState([])

  //something about this empty array as a dependency lets it hook into the
  //onMount or soemthing?
  React.useEffect(() => {
    const unsubscribe = getExpenses()
  }, [])

  function getExpenses() {
    // firebase.db
    //   .collection('expenses')
    //   .orderBy('created', 'desc')
    //   .onSnapshot(handleSnapshot)

    firebase.db
      .collection('expensesNew').doc(user.uid)
      .orderBy('created', 'desc')
      .onSnapshot(handleSnapshot)
  }

  function handleSnapshot(snapshot) {
    const expenses = snapshot.docs.map(doc => {
      return { id: doc.id, ...doc.data() }
    })
    setExpenses(expenses)
  }

  //ok, so can i determine who the logged in user is here? of course i can
  //how do i do that
  console.log(user && user.uid)
  //so i think i can get all expenses by user id... from the snapshot above
  //instead of handling all, i can just

  return (
    <div>
      {/* {postedByAuthUser && */}
        <div className="expenses-list">
          {expenses.map((expense, index) => (
            <Expense key={expense.id} expense={expense} index={index + 1} />
          ))}
        </div>
      {/* } */}
    </div>
  )
}

export default Expenses
