  }

  return (
    <Router>
      <FirebaseContext.Provider value={{ user, preferences }}>
        <Nav />
        <section className="content-area">
          {user &&
            <>
              <Route path="/product/" component={Product} />
            </>
          }
          <Route path="/" exact component={Home} />
        </section>
      </FirebaseContext.Provider>
    </Router>
  )
}

export default Main
